import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Image, TextInput, Alert } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';


async function EditUser({navigation}) {
  const [first_name, setFirstName] = useState('');
  const [last_name,setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  useEffect(() => {
    // Fetch user details from AsyncStorage and update the state
    AsyncStorage.getItem('userDetails').then((data) => {
      if (data !== null) {
        const { first_name,last_name, email, password } = JSON.parse(data);
        setFirstName(first_name);
        setLastName(last_name);
        setEmail(email);
        setPassword(password);

      }
    });
  }, []); 


  const handleUpdate = async () => {
    try {
      const response = await fetch(`http://localhost:3333/api/1.0.0/user/${id}`, {
        method: 'PATCH',
        headers: {
            'X-Authorization': token,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ first_name, last_name, email, password }),
      });

      const data = await response.json();
      navigation.navigate('Login');
      console.log(data);
    } catch (error) {
      console.error(error);
    }
  };



    // Save user details to AsyncStorage
    const userDetails = { first_name, last_name, email, password };
    await AsyncStorage.setItem('userDetails', JSON.stringify(userDetails));



  return (
    <View style={styles.container}>
      <Image
        source={require('./assets/whatsthat.png')}
        style={{ width: 100, height: 100, display: 'flex', marginLeft: 30, marginBottom: 30 }}
      />
      <Text style={styles.title}>Edit Details</Text>

      <Text style={styles.label}>First Name:</Text>
      <TextInput
        style={styles.input}
        placeholder="Enter your First Name"
        value={first_name}
        onChangeText={(text) => setFirstName(text)}
      />
       <Text style={styles.label}>Last Name:</Text>
      <TextInput
        style={styles.input}
        placeholder="Enter your Last Name"
        value={last_name}
        onChangeText={(text) => setLastName(text)}
      />

      <Text style={styles.label}>Email:</Text>
      <TextInput
        style={styles.input}
        placeholder="Enter your email"
        value={email}
        onChangeText={(text) => setEmail(text)}
      />

      <Text style={styles.label}>Password:</Text>
      <TextInput
        style={styles.input}
        placeholder="Enter your password"
        value={password}
        secureTextEntry={true}
        onChangeText={(text) => setPassword(text)}
      />

      <TouchableOpacity style={styles.button} onPress={handleUpdate}>
        <Text style={styles.buttonText}>Save Changes</Text>
      </TouchableOpacity>
      <TouchableOpacity
        style={styles.button}
        onPress={() => navigation.goBack()}
      >
        <Text style={styles.buttonText}>Back</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#808000',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',

    marginBottom: 20,
  },
  label: {
    fontSize: 16,
    fontWeight: 'bold',
    marginTop: 10,
    alignSelf: 'flex-start',
    marginLeft: 20,
  },
  input: {
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 5,
    padding: 10,
    margin: 10,
    width: 250,
    fontSize: 16,
    color: 'white',
  },
  button: {
    backgroundColor: 'white',
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 5,
    marginTop: 20,
    color: 'green',
  },
  buttonText: {
    color: 'black',
    fontSize: 16,
    fontWeight: 'bold',
  },
});

export default EditUser;
