import React, { useState, useEffect } from 'react'
import {
  View,
  Text,
  StyleSheet,
  TextInput,
  Alert,
  TouchableOpacity,
} from 'react-native'
import { useNavigation, HeaderBackButton } from '@react-navigation/native'
import { Ionicons } from '@expo/vector-icons'
import AsyncStorage from '@react-native-async-storage/async-storage'
import { HeaderTitle } from '@react-navigation/elements'

function Chats() {
  const navigation = useNavigation()
  const [message, setMessage] = useState('')
  const [chatMessages, setChatMessages] = useState([])
  const { chat_id, setChatId } = useState

  async function fetchChatMessages() {
    try {
      const token = await AsyncStorage.getItem('token')
      const response = await fetch(
        `http://localhost:3333/api/1.0.0/chat/${chat_id}/message`,
        {
          headers: {
            Authorization: token,
          },
        }
      )
      const data = await response.json()
      setChatMessages(data)
      console.log(data, 'This is chat message')
      setChatId(data)
      console.log(data, 'This is chat id')
    } catch (error) {
      Alert.alert('Error', error.message)
    }
  }

  async function sendMessage() {
    try {
      const token = await AsyncStorage.getItem('token')
      const response = await fetch(
        `http://localhost:3333/api/1.0.0/chat/${chat_id}/message`,
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            Authorization: token,
          },
          body: JSON.stringify({ message }),
        }
      )
      if (response.ok) {
        // handle success
        setMessage('')
        fetchChatMessages()
      } else {
        throw new Error('Failed to send message')
      }
    } catch (error) {
      Alert.alert('Error', error.message)
    }
  }

  useEffect(() => {
    fetchChatMessages()
  }, [])

  return (
    <View style={styles.container}>
      {chatMessages.map((chatMessage) => (
        <Text key={chatMessage.id} style={styles.message}>
          {chatMessage.message}
        </Text>
      ))}
      <TextInput
        value={message}
        onChangeText={setMessage}
        placeholder="Type your message"
        style={styles.input}
      />
      <TouchableOpacity onPress={sendMessage}>
        <Ionicons name="send" size={24} color="white" />
      </TouchableOpacity>
      <TouchableOpacity
        style={styles.button}
        onPress={() => navigation.goBack()}
      >
        <Ionicons name="ios-backspace-sharp" size={30} color="black" />
      </TouchableOpacity>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#808000',
    padding: 20,
    alignItems: 'center',
  },
  input: {
    backgroundColor: '#fff',
    borderRadius: 5,
    padding: 10,
    width: '100%',
    marginBottom: 20,
  },
  headerTitle: {
    fontSize: 20,
    color: '#fff',
  },
  message: {
    backgroundColor: '#fff',
    borderRadius: 5,
    padding: 10,
    width: '100%',
    marginBottom: 10,
  },
})

export default Chats
