import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Image, TextInput, Alert } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import ImagePicker from 'react-native-image-picker';
import FileSelectorManager from 'react-native-file-selector';



function EditUser({  navigation }) {
  const [first_name, setFirstName] = useState(first_name);
  const [last_name,setLastName] = useState(last_name);
  const [email, setEmail] = useState(email);
  const [password, setPassword] = useState(password);
  const [imageSource, setImageSource] = useState(imageSource);

  const options = {
    mediaType: 'photo',
    includeBase64: false,
    quality: 0.5
  };

  useEffect(() => {


    // Fetch user details from AsyncStorage and update the state
    AsyncStorage.getItem('first_name').then((first_name) => {
      if (first_name !== null) {
        setFirstName(first_name);
      }
    })

    AsyncStorage.getItem('last_name').then((last_name) => {
        if (last_name !== null) {
          setLastName(last_name);
        };
      })
      AsyncStorage.getItem('email').then((email) => {
        if (email !== null) {
          setEmail(email);
        }
      })

  }, []);

  const handleImageSelect = () => {
    FileSelectorManager.showFileSelector('.png,.jpg,.jpeg', file => {
      const selectedImage = file.uri;
      // do something with the selected image
    });
  };



  const handleSubmit = async () => {
    // Validate form fields
    if (!first_name || !last_name || !email || !password) {
      Alert.alert('Error', 'Please fill all fields');
      return;
    }

    // Get user id and token from AsyncStorage
    const token = await AsyncStorage.getItem('@token');
    const id = await AsyncStorage.getItem('@id');

    // Make API call to update user details
    const url = `http://localhost:3333/api/1.0.0/user/${id}`;
    const body = { first_name, last_name, email, password };
    const options = {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
        'X-Authorization': token,
      },
      body: JSON.stringify(body),
    };

    try {
      const response = await fetch(url, options);
      const data = await response.json();
      console.log(data); // Do something with the API response
    } catch (error) {
      console.error(error);
      Alert.alert('Error', 'Failed to update user details');
      return;
    }

    // Save user details to AsyncStorage
    const userDetails = { first_name, last_name, email, password };
    await AsyncStorage.setItem('userDetails', JSON.stringify(userDetails));

    // Show success message
    Alert.alert('Success', 'User details updated successfully');
  };

  //console.log('State:', { first_name, last_name, email, password });
  const postImage = async () => {
    // Assuming that selectedImage is the base64-encoded image string
const binaryData = atob(imageSource.split(',')[1]);
const arrayBuffer = new ArrayBuffer(binaryData.length);
const uint8Array = new Uint8Array(arrayBuffer);

for (let i = 0; i < binaryData.length; i++) {
  uint8Array[i] = binaryData.charCodeAt(i);
}

const formData = new FormData();
formData.append('image', new Blob([uint8Array]), 'image.jpg');

fetch('https://example.com/api/upload-image', {
  method: 'POST',
  body: formData,
})
  .then(response => response.json())
  .then(data => {
    console.log(data);
  })
  .catch(error => {
    console.error(error);
  });

  };

  const handleInputImage = (event) => {
    const selectedFile = event.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      setImageSource(reader.result);
      // do something with the selected image
    };

  };




  return (
    <View style={styles.container}>
       <View style={styles.container}>
        <input type="file" accept=".png, .jpg, .jpeg" onChange={handleInputImage} />

      {imageSource && (
        <Image source={imageSource} style={styles.image} />
      )}
      <button onPress={() => postImage}>upload image </button>
    </View>

      <Text style={styles.title}>Edit Details</Text>

      <Text style={styles.label}>First Name:</Text>
      <TextInput
        style={styles.input}
        defaultValue={first_name}
        placeholder={first_name}
        value={first_name}
        onChangeText={(text) => setFirstName(text)}
      />
       <Text style={styles.label}>Last Name:</Text>
      <TextInput
        style={styles.input}
        defaultValue={last_name}
        placeholder={last_name}
        value={last_name}
        onChangeText={(text) => setLastName(text)}
      />

      <Text style={styles.label}>Email:</Text>
      <TextInput
        style={styles.input}
        defaultValue={email}
        placeholder={email}
        value={email}
        onChangeText={(text) => setEmail(text)}
      />

      <Text style={styles.label}>Password:</Text>
      <TextInput
        style={styles.input}
        placeholder={password}
        defaultValue={password}
        value={password}
        secureTextEntry={true}
        onChangeText={(text) => setPassword(text)}
      />

      <TouchableOpacity style={styles.button} onPress={handleSubmit}>
        <Text style={styles.buttonText}>Save Changes</Text>
      </TouchableOpacity>

      <TouchableOpacity
        style={styles.button}
        onPress={() => navigation.goBack()}
      >
        <Text style={styles.buttonText}>Back</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#808000',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  label: {
    fontSize: 16,
    fontWeight: 'bold',
    marginTop: 10,
    alignSelf: 'flex-start',
    marginLeft: 20,
  },
  input: {
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 5,
    padding: 10,
    margin: 10,
    width: 250,
    fontSize: 16,
    color: 'white',
  },
  button: {
    backgroundColor: 'white',
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 5,
    marginTop: 20,
    color: 'green',
  },
  buttonText: {
    color: 'black',
    fontSize: 16,
    fontWeight: 'bold',
  },
  image: {
    width: 200,
    height: 200,
    marginTop: 20,
  },
});

export default EditUser;
