import React from 'react'
import { View, Text, StyleSheet, Touchable } from 'react-native'
import { TouchableOpacity } from 'react-native-gesture-handler'

function AddContact() {
  const Add = async () => {
    try {
      const response = await fetch(
        'http://localhost:3333/api/1.0.0/user/' + user_id + '/contact',
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ user_id }),
        }
      )

      const data = await response.json()
      console.log(data)
      // Save the token in local storage
      await AsyncStorage.setItem('@id', data.id)
      fetchUser()
    } catch (error) {
      console.error(error)
    }
  }

  return (
    <View style={styles.container}>
      <StatusBar style="auto" />

      <View style={styles.inputView}>
        <TextInput
          style={styles.TextInput}
          placeholder="ID"
          placeholderTextColor="#003f5c"
          secureTextEntry={true}
          onChangeText={(user_id) => setUserId(user_id)}
        />
      </View>
      <TouchableOpacity style={styles.signupbtn} onPress={Add}>
        <Text style={styles.signuptxt}>Login</Text>
      </TouchableOpacity>
      <Text style={styles.loginP}>Do not have an account?</Text>
      <TouchableOpacity onPress={() => navigation.navigate('Signup')}>
        <Text style={styles.loginP}>Sign Up here</Text>
      </TouchableOpacity>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#808000',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  button: {
    backgroundColor: 'white',
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 100,
    marginTop: 50,
    color: 'green',
  },
  buttonText: {
    color: 'black',
    fontSize: 16,
    fontWeight: 'bold',
  },
})

export default AddContact
