import React, { useState, useEffect } from 'react'
import { useNavigation, HeaderBackButton } from '@react-navigation/native'
import { Ionicons } from '@expo/vector-icons'
import AsyncStorage from '@react-native-async-storage/async-storage'
import {
  View,
  Text,
  StyleSheet,
  TextInput,
  TouchableOpacity,
} from 'react-native'

function AddMember(props) {
  const [user_id, setUserId] = useState('')
  const [chat_id, setChatId] = useState('')
  useEffect(() => {
    console.log(props, 'chatId')
    setChatId(props.route.params.chat_id)
    console.log(chat_id, 'chat iddd')
  }, [])

  const navigation = useNavigation()
  const Add = async () => {
    try {
      const token = await AsyncStorage.getItem('@token')

      if (!user_id) {
        console.log('User ID is empty')
        return
      }

      fetch(`http://localhost:3333/api/1.0.0/chat/${chat_id}/user/${user_id}`, {
        method: 'POST',
        headers: {
          'X-Authorization': token,
        },
      }).then(async (response) => {
        const responseBody = await response.text()
        console.log(responseBody) // log response body
        // return response.json()
      })
    } catch (error) {
      console.log(error)
    }
  }

  return (
    <View style={styles.container}>
      <View style={styles.formContainer}>
        <View style={styles.inputView}>
          <Text style={styles.title}>Enter ID </Text>
          <TextInput
            style={styles.TextInput}
            placeholder="Enter user ID"
            placeholderTextColor="#003f5c"
            onChangeText={(user_id) => setUserId(user_id)}
          />
        </View>
        <TouchableOpacity style={styles.button} onPress={Add}>
          <Text style={styles.buttonText}>Add</Text>
        </TouchableOpacity>
      </View>
      <TouchableOpacity
        style={styles.backButton}
        onPress={() => navigation.goBack()}
      >
        <Ionicons name="ios-backspace-sharp" size={30} color="black" />
      </TouchableOpacity>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'white',
  },
  formContainer: {
    width: '80%',
    marginVertical: 50,
    justifyContent: 'center',
    alignItems: 'center',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  inputView: {
    marginBottom: 20,
  },
  TextInput: {
    height: 50,
    width: '100%',
    borderColor: 'gray',
    borderWidth: 1,
    borderRadius: 10,
    padding: 10,
    fontSize: 18,
    color: 'white',
  },
  button: {
    backgroundColor: 'white',
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 100,
    marginTop: 20,
    color: 'green',
  },
  buttonText: {
    color: 'black',
    fontSize: 16,
    fontWeight: 'bold',
  },
  backButton: {
    position: 'absolute',
    bottom: 30,
    left: 20,
  },
})

export default AddMember
