import React, { useState, useEffect } from 'react'
import { useNavigation, HeaderBackButton } from '@react-navigation/native'
import { Ionicons } from '@expo/vector-icons'
import AsyncStorage from '@react-native-async-storage/async-storage'
import {
  View,
  Text,
  StyleSheet,
  TextInput,
  TouchableOpacity,
} from 'react-native'

function AddMember(props) {
  const [user_id, setUserId] = useState('')
  const [chat_id, setChatId] = useState('')
  useEffect(() => {
    setChatId(props.route.params.chat_id)
  }, [])

  const navigation = useNavigation()

  const handleAdd = async () => {
    try {
      const token = await AsyncStorage.getItem('@token')

      if (!user_id) {
        console.log('User ID is empty')
        return
      }

      const response = await fetch(
        `http://localhost:3333/api/1.0.0/chat/${chat_id}/user/${user_id}`,
        {
          method: 'POST',
          headers: {
            'X-Authorization': token,
          },
        }
      )

      const responseBody = await response.text()
      console.log(responseBody) // log response body
    } catch (error) {
      console.log(error)
    }
  }

  return (
    <View style={styles.container}>
      <View style={styles.inputView}>
        <Text style={styles.title}>Enter User ID</Text>
        <TextInput
          style={styles.input}
          placeholder="Type here..."
          placeholderTextColor="#ccc"
          onChangeText={(user_id) => setUserId(user_id)}
        />
      </View>

      <TouchableOpacity style={styles.button} onPress={handleAdd}>
        <Text style={styles.buttonText}>Add Member</Text>
      </TouchableOpacity>

      <TouchableOpacity
        style={styles.backButton}
        onPress={() => navigation.goBack()}
      >
        <Ionicons name="ios-arrow-back" size={24} color="white" />
      </TouchableOpacity>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    paddingHorizontal: 20,
    paddingTop: 50,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  inputView: {
    marginBottom: 20,
  },
  input: {
    height: 50,
    borderWidth: 1,
    borderColor: '#ccc',
    paddingHorizontal: 15,
    borderRadius: 10,
    fontSize: 16,
    color: '#000',
  },
  button: {
    backgroundColor: '#808000',
    paddingVertical: 15,
    paddingHorizontal: 20,
    borderRadius: 10,
    alignItems: 'center',
  },
  buttonText: {
    color: '#fff',
    fontSize: 18,
    fontWeight: 'bold',
  },
  backButton: {
    position: 'absolute',
    top: 50,
    left: 20,
    backgroundColor: '#808000',
    padding: 10,
    borderRadius: 50,
  },
})
export default AddMember
