import React, { useState, useEffect } from 'react'
import { View, Text, StyleSheet, TouchableOpacity, Image } from 'react-native'
import { TextInput } from 'react-native-gesture-handler'
import AsyncStorage from '@react-native-async-storage/async-storage'
import { useNavigation } from '@react-navigation/native'

function Singlechat() {
  const [chat_id, setChatId] = useState('')
  const [chat, setChat] = useState({
    name: '',
    creator: { user_id: '', first_name: '', last_name: '', email: '' },
    members: { user_id: '', first_name: '', last_name: '' },
    messages: {},
  })

  const navigation = useNavigation()

  const fsinglechat = async () => {
    const token = await AsyncStorage.getItem('@token')

    fetch('http://localhost:3333/api/1.0.0/chat/' + chat_id, {
      method: 'GET',
      headers: {
        'X-Authorization': token,
        'Content-Type': 'application/json',
      },
    })
      .then((response) => response.json())
      .then((data) => {
        setChat(data)
        console.log(data.creator, 'data creator')
      })
      .catch((error) => console.log(error))
  }

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Single Chat Details</Text>
      <TextInput
        style={styles.input}
        defaultValue={chat_id}
        placeholder="Enter ID Number"
        value={chat_id}
        onChangeText={(text) => setChatId(text)}
      />
      <TouchableOpacity style={styles.button} onPress={fsinglechat}>
        <Text style={styles.buttonText}>Get chat</Text>
      </TouchableOpacity>
      {chat && (
        // render the chat details once chat state is not null
        <View>
          <Text style={styles.title}>Chat Details</Text>
          <View style={styles.listItem}>
            <Text style={styles.label}>Chat Name:</Text>
            <Text style={styles.value}>{chat.name}</Text>
          </View>
          <View style={styles.listItem}>
            <Text style={styles.label}>Creator ID:</Text>
            <Text style={styles.value}>{chat.creator.user_id}</Text>
          </View>
          <View style={styles.listItem}>
            <Text style={styles.label}>Creator Name:</Text>
            <Text style={styles.value}>
              {chat.creator.first_name} {chat.creator.last_name}
            </Text>
          </View>
          <View style={styles.listItem}>
            <Text style={styles.label}>Creator Email:</Text>
            <Text style={styles.value}>{chat.creator.email}</Text>
          </View>
          <View style={styles.listItem}>
            <Text style={styles.label}>Last Message:</Text>
            <Text style={styles.value}>{chat.messages}</Text>
          </View>
        </View>
      )}
      <TouchableOpacity
        style={styles.button}
        onPress={() => navigation.goBack()}
      >
        <Text style={styles.buttonText}>Back</Text>
      </TouchableOpacity>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#808000',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
    color: 'white',
  },
  listItem: {
    flexDirection: 'row',
    alignItems: 'center',
    marginTop,

    label: {
      fontSize: 16,
      fontWeight: 'bold',
      marginTop: 10,
      alignSelf: 'flex-start',
      marginLeft: 20,
    },
    input: {
      borderWidth: 1,
      borderColor: '#ccc',
      borderRadius: 5,
      padding: 10,
      margin: 10,
      width: 250,
      fontSize: 16,
      color: 'white',
      placeholder: {
        fontSize: 50,
      },
    },
    error: {
      fontSize: 16,
      color: 'red',
    },
    button: {
      backgroundColor: 'white',
      paddingVertical: 10,
      paddingHorizontal: 20,
      borderRadius: 5,
      marginTop: 20,
      color: 'green',
    },
    buttonText: {
      color: 'black',
      fontSize: 16,
      fontWeight: 'bold',
    },
  },
})

export default Singlechat
