import React, { useState, useEffect } from 'react'
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Alert,
} from 'react-native'
import { useNavigation } from '@react-navigation/native'
import { Ionicons } from '@expo/vector-icons'
import AsyncStorage from '@react-native-async-storage/async-storage'

function ViewContact() {
  const [user, setUser] = useState('')
  const [contactList, setContactList] = useState([])
  const navigation = useNavigation()

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const token = await AsyncStorage.getItem('@token')

        const response = await fetch(
          'http://localhost:3333/api/1.0.0/contacts',
          {
            method: 'GET',
            headers: {
              'X-Authorization': token,
              'Content-Type': 'application/json',
            },
          }
        )

        if (response.status === 401) {
          Alert.alert(
            'Unauthorized',
            'You are not authorized to view this page.'
          )
          return
        }

        const data = await response.json()
        setContactList(data)
      } catch (error) {
        console.error(error)
      }
    }
    fetchUser()
  }, [])

  const handleDelete = async (contactId) => {
    try {
      const token = await AsyncStorage.getItem('@token')

      const response = await fetch(
        `http://localhost:3333/api/1.0.0/contact/${contactId}`,
        {
          method: 'DELETE',
          headers: {
            'X-Authorization': token,
            'Content-Type': 'application/json',
          },
        }
      )

      if (response.status === 401) {
        Alert.alert(
          'Unauthorized',
          'You are not authorized to delete this contact.'
        )
        return
      }

      if (response.status === 200) {
        Alert.alert('Success', 'Contact deleted successfully.')
        setContactList(
          contactList.filter((contact) => contact.id !== contactId)
        )
      } else if (response.status === 404) {
        Alert.alert('Error', 'Contact not found.')
      } else {
        throw 'Something went wrong'
      }
    } catch (error) {
      console.log(error)
      Alert.alert('Error', 'Something went wrong.')
    }
  }

  return (
    <View style={styles.container}>
      <ScrollView contentContainerStyle={styles.scrollContainer}>
        <Text style={styles.title}>Contacts</Text>
        <View style={styles.field}>
          {contactList.map((contact) => (
            <View key={contact.id} style={styles.contact}>
              <View style={styles.contactInfo}>
                <Text style={styles.contactName}>
                  {contact.first_name} {contact.last_name}
                </Text>
                <Text style={styles.contactEmail}>{contact.email}</Text>
              </View>
              <TouchableOpacity
                style={styles.deleteButton}
                onPress={() => handleDelete(contact.id)}
              >
                <Ionicons name="trash-outline" size={24} color="white" />
              </TouchableOpacity>
            </View>
          ))}
        </View>
        <TouchableOpacity
          style={styles.button}
          onPress={() => navigation.goBack()}
        >
          <Ionicons name="ios-backspace-sharp" size={30} color="black" />
        </TouchableOpacity>
      </ScrollView>
    </View>
  )
}

const styles = StyleSheet.create({
  scrollContainer: {
    flexGrow: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#808000',
  },
  container: {
    flex: 1,
    justifyContent: 'center',

    alignItems: 'center',
    backgroundColor: '#808000',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
    marginTop: 50,
  },
  button: {
    backgroundColor: 'white',
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 100,
    marginTop: 50,
    color: 'green',
    marginBottom: 50,
  },
  buttonText: {
    color: 'black',
    fontSize: 16,
    fontWeight: 'bold',
  },
  field: {
    borderWidth: 1,
    borderColor: 'white',
    borderRadius: 5,
    padding: 30,
    marginTop: 40,
    width: '90%',
    justifyContent: 'center',
    alignItems: 'center',
    color: 'white',
  },
  contact: {
    backgroundColor: 'white',
    padding: 10,
    marginBottom: 10,
    borderRadius: 5,
    width: '100%',
  },
  contactName: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  contactInfo: {
    fontSize: 14,
  },
})

export default ViewContact
