import React, { useState, useEffect } from 'react'
import { View, Text, StyleSheet } from 'react-native'
import { TouchableOpacity } from 'react-native-gesture-handler'
import { useNavigation } from '@react-navigation/native'
import AsyncStorage from '@react-native-async-storage/async-storage'

function ChatsScreen(props) {
  const navigation = useNavigation()
  const [user, setUser] = useState(null)

  const [chatList, setChatList] = useState([])

  const viewchat = async () => {
    try {
      const token = await AsyncStorage.getItem('@token')

      const response = await fetch('http://localhost:3333/api/1.0.0/chat', {
        method: 'GET',
        headers: {
          'X-Authorization': token,
          'Content-Type': 'application/json',
        },
      })
      const data = await response.json()
      console.log(date, 'this is date')
      setChatList(data)
      console.log(chatList, ' this is chatlist')
    } catch (error) {
      console.error(error)
    }
  }
  useEffect(() => {
    viewchat()
  }, [])

  const addchat = () => {
    navigation.navigate('addchat')
  }
  return (
    <View style={styles.container}>
      <Text style={styles.title}>Chats</Text>

      <TouchableOpacity style={styles.button} onPress={addchat}>
        <Text style={styles.buttonText}>Add Chat</Text>
      </TouchableOpacity>
      {/* <Text style={styles.label}>Chat</Text>
      {name.name && (
        <TextInput style={styles.input} value={} editable={false} /> */}
      {/* )} */}
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#808000',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
})

export default ChatsScreen
